import json
import os

SCENARIOS_DIR = "/home/anicka/karma-electric/data/scenarios/emptiness-applied"
RESPONSES_DIR = "/home/anicka/karma-electric/data/practice-responses/emptiness-applied"

# Ensure directories exist
os.makedirs(SCENARIOS_DIR, exist_ok=True)
os.makedirs(RESPONSES_DIR, exist_ok=True)

def write_scenario(scenario_id, category, user_message, pattern, teaching_target):
    """Write scenario file."""
    scenario = {
        "scenario_id": scenario_id,
        "category": category,
        "user_message": user_message,
        "pattern": pattern,
        "teaching_target": teaching_target
    }
    filepath = os.path.join(SCENARIOS_DIR, f"{scenario_id}.json")
    with open(filepath, 'w') as f:
        json.dump(scenario, f, indent=2)

def write_response(response_id, scenario_id, response_text):
    """Write response file."""
    response = {
        "response_id": response_id,
        "scenario_id": scenario_id,
        "response": response_text
    }
    filepath = os.path.join(RESPONSES_DIR, f"{response_id}.json")
    with open(filepath, 'w') as f:
        json.dump(response, f, indent=2)

